sys_variable_value.value:
(function execute(inputs, outputs) {
	var inc = new GlideRecord('incident');
	if (inc.get('number', inputs.incident_number)) {
		var serviceList = inc.business_service;

		if (serviceList) {
			var sysIds = serviceList.split(',');
			if (sysIds.length > 0) {
				var names = [];
				var emails = [];

				var svc = new GlideRecord('cmdb_ci_service_auto');
				svc.addQuery('sys_id', 'IN', sysIds);
				svc.query();

				while (svc.next()) {
					var owner = svc.owned_by;
					if (owner) {
						names.push(owner.getDisplayValue());
						emails.push(owner.email.toString());
					}
				}

				outputs.owner_name = names.join(', ');
				outputs.owner_email = emails.join(', ');
			} else {
				outputs.owner_name = 'No services found';
				outputs.owner_email = '';
			}
		} else {
			outputs.owner_name = 'No business services linked';
			outputs.owner_email = '';
		}
	} else {
		outputs.owner_name = 'Incident not found';
		outputs.owner_email = '';
	}
})(inputs, outputs);

